arguments.argument:
if image_index < image_number - 1.25 {image_index += 0.25;}
if place_meeting(x, y + 1, o_solid) {gravity = 0; vspeed = 0; hspeed = hspeed/1.5;} else {gravity = 0.3;}
if place_meeting(x, y - 2, o_solid) {vspeed = 0;}
if place_meeting(x + hspeed, y, o_solid) {hspeed = 0;}
if place_meeting(x, y, o_liq) && vspeed > 2 {vspeed = 2;}
if x < 0 || y < 0 || x > global.map_w || y > global.map_h {instance_destroy();}
if place_meeting(x, y, o_lift_up) {vspeed -= 0.5;}
if place_meeting(x, y, o_lift_down) {vspeed += 0.5;}
