arguments.argument:
if speed > 1.5 {image_angle = direction;}
if place_meeting(x, y + 1, o_solid) || place_meeting(x, y + 1, o_jthr)  {gravity = 0; hspeed = hspeed/2;} else {gravity = 0.3;}
if place_meeting(x, y + vspeed, o_solid) || place_meeting(x, y + vspeed, o_jthr) {vspeed = -vspeed/2; if speed > 1 {snd_play_pos(21, x, y);}}
if place_meeting(x, y, o_liq) {vspeed -= 0.25;}
if place_meeting(x + hspeed, y, o_solid) {hspeed = -hspeed/2; if speed > 1 {snd_play_pos(21, x, y);}}
if x < 0 || y < 0 || x > global.map_w || y > global.map_h {instance_destroy();}
if place_meeting(x, y, o_lift_up) {vspeed -= 0.5;}
if place_meeting(x, y, o_lift_down) {vspeed += 0.5;}
