arguments.argument:
if !variable_global_exists('cl_inst'){exit;}
if !instance_exists(global.cl_inst){exit;}
if global.cl_inst.x < -256 {exit;}

//because of width and height shit need to manage views manually
view_xview = global.cl_inst.x - 512 + bkoffset / 2;
view_yview = global.cl_inst.y - 389 + offset;
if view_xview > global.map_w - 1024 + bkoffset {view_xview = global.map_w - 1024 + bkoffset;}
if view_yview > global.map_h - 768 {view_yview = global.map_h - 768;}
if view_xview < 0 {view_xview = 0;}
if view_yview < 0 {view_yview = 0;}

if instance_exists(o_menu) {exit;}
if global.dem_mode >= 2 {exit;}
if keyboard_check(global.key_up) && offset > -192 && !global.con {offset -= 4;}
if keyboard_check(global.key_dn) && offset < 192 && !global.con {offset += 4;}
if !keyboard_check(global.key_up) && !keyboard_check(global.key_dn) && offset != 0 {offset -= 4 * sign(offset);}
