{
  if global.con == 1
  {
    global.con = 0; 
    keyboard_string = '';
  }
  else 
  {
    saymode = 0;
    keyboard_string = '';
    global.con = 1; 
  }
}
if keyboard_check_pressed(global.key_chat) && room == rm_game && !saymode && !global.con {keyboard_string = ''; saymode = 1;}
if keyboard_check_pressed(global.key_scr) 
{
    con_add(":: SYSTEM: Wrote screenshot " + 'screenshots\scr' + string(current_day) + string(current_month) + string(current_year) + string(current_hour) + string(current_minute) + string(current_second) + '.png.');
    screen_save('screenshots\scr' + string(current_day) + string(current_month) + string(current_year) + string(current_hour) + string(current_minute) + string(current_second) + '.png');
}
if (saymode || global.con) && string_length(keyboard_string) > 96 {keyboard_string = string_delete(keyboard_string, 97, string_length(keyboard_string));}
