    {
        draw_set_alpha(pain_alpha);
        draw_rectangle_color(view_xview, view_yview, view_xview + 824, view_yview + 768, c_maroon, c_maroon, c_maroon, c_maroon, 0);
        draw_set_alpha(1);
    }

    if global.r_announcer
    {
        for (i = 1; i <= 4; i += 1)
        {
            if global.hud_text[i, 1] == '' {continue;}
            draw_set_color(global.hud_text[i, 6]);
            draw_set_halign(fa_center);
            draw_set_font(global.hud_text[i, 3]);
            draw_text(view_xview + global.hud_text[i, 4], view_yview + global.hud_text[i, 5], global.hud_text[i, 1]);
            draw_set_font(-1);
            draw_set_halign(fa_left);
            draw_set_color(c_white);
        }
    }
}

draw_set_font(global.fnt_sys);

draw_text(view_xview + 832, view_yview + 208, global.sv_name + "#Карта: " + global.map_name + "#Лимит очков: " + string(global.mp_fraglimit));

