arguments.argument:
pain_alpha = max(0, pain_alpha - 0.01);

for (i = 1; i <= 4; i += 1)
{
    if global.hud_text[i, 1] == '' {continue;}
    global.hud_text[i, 2] = max(0, global.hud_text[i, 2] - 1);
    if global.hud_text[i, 2] == 0 {global.hud_text[i, 1] = '';}
}

if !variable_global_exists('cl_inst') {exit;}
if !instance_exists(global.cl_inst) {exit;}
with global.cl_inst
{
    if !variable_local_exists('dead') {exit;}
    if !dead {FMODListenerSet3dPosition(1, x, y, 0);} else {FMODListenerSet3dPosition(1, view_xview + 512, view_yview + 384, 0); with o_pl {dname = true; alarm[3] = global.cl_fps_max/2;}}

}
