arguments.argument:
if global.cl_sync_type != 1 {exit;}

//looong condition
if !keyboard_check_pressed(global.key_left) {
  if !keyboard_check_pressed(global.key_right) {
    if !keyboard_check_pressed(global.key_up) {
      if !keyboard_check_pressed(global.key_dn) {
        if !keyboard_check_pressed(global.key_jmp) {
          exit;
        }
      }
    }
  }
}

control_send();
