arguments.argument:
if !sprite_exists(sprites[spr, 0]) {exit;}
if !variable_local_exists('cl_color') {exit;}
if !variable_global_exists('cl_inst') {exit;}
if dead {exit;}
if !pain && r_weapon {draw_sprite_ext(s_wpns, w, x, y, image_xscale, image_yscale, w_rot, c_white, 1);}
if hp == 250 && image_alpha == 1 {image_alpha = 0.25;}
if hp < 250 && image_alpha == 0.25 {image_alpha = 1;}
draw_sprite_ext(sprites[spr, 0], image_index, x, y, image_xscale, image_yscale, 0, c_white, image_alpha);
draw_sprite_ext(sprites[spr, 1], image_index, x, y, image_xscale, image_yscale, 0, cl_color, image_alpha);

if id == global.cl_inst || !global.r_names {exit;}
draw_set_font(global.fnt_sys);
draw_set_halign(fa_center);
draw_text(x, y - 48, cl_name);
draw_set_halign(fa_left);
draw_set_font(-1);
