arguments.argument:
draw_set_font(-1);
ed_spr_select();
draw_set_color(c_white);
if (mouse_check_button(mb_left) || keyboard_check(vk_space)) && keyboard_check(vk_shift) && API_Combobox_GetSel(ui_tl_cb) < 11
{
  ed_draw_panel();
  draw_rectangle_color(x2 - 3, y2 - 3, x2 + 3, y2 + 3, c_blue, c_blue, c_blue, c_blue, 0);
}
else
{
  draw_sprite_ext(spr, im, round_mouse_x() + x_off - ix_off, round_mouse_y() + y_off - iy_off, 1, 1, 0, c_white, 0.73);  
  if API_Combobox_GetSel(ui_tl_cb) < 11 && !mouse_check_button(mb_right)
  {
    draw_set_color(col);
    draw_set_alpha(0.73);
    draw_rectangle(round_mouse_x() + x_off, round_mouse_y() + y_off,
                   round_mouse_x() + x_off + sprite_get_width(spr) - 1,
                   round_mouse_y() + y_off + sprite_get_height(spr) - 1, 1);
    draw_set_alpha(1);
    draw_set_color(c_white);
  }
}
if mouse_check_button(mb_right) && keyboard_check(vk_shift) 
{
  draw_set_color(col);
  draw_rectangle(x1, y1, x2, y2, 1);
  draw_set_color(c_white);
  draw_rectangle_color(x2 - 3, y2 - 3, x2 + 3, y2 + 3, c_blue, c_blue, c_blue, c_blue, 0);
}

var mx, my;
if mouse_x > view_xview + view_wview - 64 {mx = -40;} else {mx = 16;}
if mouse_y > view_yview + view_hview - 64 {my = -32;} else {my = 0;}

draw_set_alpha(0.75);
draw_text(mouse_x + mx, mouse_y + my,
          string(round_mouse_x() + x_off - ix_off) + "#" +
          string(round_mouse_y() + y_off - iy_off));
draw_set_alpha(1);

if global.ed_mode != 2 {exit;}
if !instance_exists(trg) {exit;}
draw_line_color(trg.x, trg.y, round_mouse_x() + x_off, round_mouse_y() + y_off, c_lime, c_lime);
if trg.t_id == 7 && global.ed_mode == 2
{
  draw_sprite_ext(s_spawn, 0, round_mouse_x() + x_off - 16, round_mouse_y() + y_off - 32, 1, 1, 0, c_lime, 0.57);
}
