{
  ed_draw_panel();
  draw_rectangle_color(x2 - 3, y2 - 3, x2 + 2, y2 + 2, c_blue, c_blue, c_blue, c_blue, 0);
}
else
{
  draw_sprite_ext(spr, im, round(floor(mouse_x/global.ed_g_sz)*global.ed_g_sz + x_off), round(floor(mouse_y/global.ed_g_sz)*global.ed_g_sz + y_off), 1, 1, 0, c_white, 0.73);  
  if API_Combobox_GetSel(ui_tl_cb) < 11 && !mouse_check_button(mb_right)
  {
    draw_set_color(col);
    draw_set_alpha(0.73);
    draw_rectangle(round(floor(mouse_x/global.ed_g_sz)*global.ed_g_sz + x_off), round(floor(mouse_y/global.ed_g_sz)*global.ed_g_sz + y_off), round(floor(mouse_x/global.ed_g_sz)*global.ed_g_sz + x_off) + sprite_get_width(spr) - 1, round(floor(mouse_y/global.ed_g_sz)*global.ed_g_sz + y_off) - 1 + sprite_get_height(spr), 1);
    draw_set_alpha(1);
    draw_set_color(c_white);
  }
}
if mouse_check_button(mb_right) && keyboard_check(vk_shift) 
{
    draw_set_color(col);
    draw_rectangle(x1, y1, x2, y2, 1);
    draw_set_color(c_white);
    draw_rectangle_color(x2 - 3, y2 - 3, x2 + 2, y2 + 2, c_blue, c_blue, c_blue, c_blue, 0);
