arguments.argument:
//timelimit
if global.sv_cycle_maps
{
    plr_send_gameover();
    sleep(60);
    with (o_pl) {plr_send_kick(cl_id, "Game over.");}
    global.sv_map = global.map_list_next;
    cfg_write('server.cfg');
    sleep(60);
    if file_exists('Server.exe') {execute_program('Server.exe', -1, 0);}
    if file_exists("d2dmp_sv.exe") {execute_program('d2dmp_sv.exe', -1, 0);}
    game_end();
}
else
{
    plr_send_gameover();
    sleep(60);
    with (o_pl) {plr_send_kick(cl_id, 'Server shutting down.');}
    sleep(60);
    game_end();
}
