arguments.argument:
if !global.vote_now {exit;}
var votes_needed;
votes_needed = ceil(instance_number(o_pl)/2);
with o_pl
{
    if variable_local_exists("cl_is_bot") && !voted
    {
        net_vote();
    }
}
if global.vote_num >= votes_needed
{
    net_vote_pass(true);
}
else
{
    net_vote_pass(false);
}
