arguments.argument:
//team balance
var red, blu;
red = team_count(1);
blu = team_count(2);

if red > blu + 1
{
    with o_pl
    {
        if cl_team == 1
        {
            cl_team = 2;
            cl_color = c_blue;
            plr_send_team();
            plr_send_skin();
            plr_kill(cl_id);
            dyclearbuffer(0);
            dywritebyte(5, 0);
            dywritestring(':: SERVER: Вы переведены в другую команду для баланса.', 0);
            dysendmessage(cl_tcp, 0, 0, 0);
            break;
        }
    }
}

if blu > red + 1
{
    with o_pl
    {
        if cl_team == 2
        {
            cl_team = 1;
            cl_color = c_red;
            plr_send_team();
            plr_send_skin();
            plr_kill(cl_id);
            dyclearbuffer(0);
            dywritebyte(5, 0);
            dywritestring(':: SERVER: Вы переведены в другую команду для баланса.', 0);
            dysendmessage(cl_tcp, 0, 0, 0);
            break;
        }
    }
}
