arguments.argument:
//team balance
var red, blu;
red = team_count(1);
blu = team_count(2);

if red > blu + 1
{
    with o_pl
    {
        if cl_team == 1
        {
            cl_team = 2;
            cl_color = c_blue;
            plr_send_team();
            plr_send_skin();
            plr_kill(cl_id);
            dll39_buffer_clear(0);
            dll39_write_byte(5, 0);
            dll39_write_string(':: SERVER: mp_autobalance переместил вас в другую команду.', 0);
            dll39_message_send(cl_tcp, 0, 0, 0);
            break;
        }
    }
}

if blu > red + 1
{
    with o_pl
    {
        if cl_team == 2
        {
            cl_team = 1;
            cl_color = c_red;
            plr_send_team();
            plr_send_skin();
            plr_kill(cl_id);
            dll39_buffer_clear(0);
            dll39_write_byte(5, 0);
            dll39_write_string(':: SERVER: mp_autobalance переместил вас в другую команду.', 0);
            dll39_message_send(cl_tcp, 0, 0, 0);
            break;
        }
    }
}
