arguments.argument:
var hit_ground, hit_player, hit_trig, ID, ID1;
hit_ground = 0;
hit_player = 0;
hit_trig = 0;
ID = -1;
ID1 = -1;

if (spd > 0)
{
    repeat(spd/2)
    {

        x += lengthdir_x(2, direction);
        y += lengthdir_y(2, direction);

        if (instance_place(x, y, o_solid) > 0)
        { spd = 0; hit_ground = true; break; }

        ID = instance_place(x, y, o_pl);
        if (ID > 0) and !(a_i_id = ID)
        { spd = 0; hit_player = true; break; }

        ID1 = instance_place(x, y, trg_atshot);
        if ID1 > 0
        { hit_trig = true; }
    }
}

if (hit_ground = true)
{
    plr_send_effect(2, x, y);
}

if (hit_player = true)
{
    if ID.cl_id == a_id {exit;}
    if ID.st_inv || global.mp_godmode {exit;}
    if !global.mp_gamemode  || global.mp_ffire || a_team != ID.cl_team  {with (ID) {plr_hurt(other.dmg);}}
    ID.killer_id = a_id;
    ID.kill_type = k_t;
    if global.mp_knockback
    {
        if ID.x < x {ID.hsp -= 1;}
        if ID.x > x {ID.hsp += 1;}   
        if ID.y > y + 8 {ID.vsp += 1;} 
        if ID.y < y - 8 {ID.vsp -= 1;} 
    }
    with (ID) {plr_send_stat(); plr_send_pain(); plr_send_speed();}
    plr_send_effect(1, x, y);
}

if hit_trig 
{
    with ID1 {event_user(0);}
}
