arguments.argument:
var hit_ground, hit_player, ID;
hit_ground = false;
hit_player = false;
ID = noone;

repeat spd
{
  x += lengthdir_x(2, direction);
  y += lengthdir_y(2, direction);

  if place_meeting(x, y, o_solid) { hit_ground = true; break; }

  ID = instance_place(x, y, o_pl);
  if ID != noone && a_i_id != ID { hit_player = true; break; }

  ID = instance_place(x, y, o_jthr);
  if ID != noone { if yprevious < ID.y && global.mp_shootjthr { hit_ground = true; break; } }

  ID = instance_place(x, y, trg_atshot);
  if ID != noone { with ID {event_user(0);} }
}

if hit_ground
{
  plr_send_effect(2, x, y);
  instance_destroy();
}

if hit_player
{
  if ID.cl_id == a_id {exit;}
  if ID.st_inv || global.mp_godmode {exit;}

  if !global.mp_gamemode || global.mp_ffire || a_team != ID.cl_team { with ID { plr_hurt(other.dmg); } }
  ID.killer_id = a_id;
  ID.kill_type = k_t;

  with ID
  {
    if global.mp_knockback { plr_speed_add( 1, round_to(other.direction, 45) ); }
    plr_send_stat();
    plr_send_pain();
    plr_send_speed();
  }

  plr_send_effect(1, x, y);
  instance_destroy();
}
