arguments.argument:
if other.x < x {other.hsp = -8;}
if other.x > x {other.hsp = 8;}   
if other.y > y {other.vsp = 8;} 
if other.y < y {other.vsp = -8;} 
if other.pt > 0 {exit;}
var dmg, dist;
dist = distance_to_object(other);
if !global.mp_godmode && !other.st_inv {dmg = 96 - dist;} else {dmg = 0;}
if dmg < 0 {dmg = 8;}
if other.ap > 0
{
    other.hp -= round(dmg/2);
    other.ap -= dmg;
}
else
{
    other.hp -= dmg;
}
if other.ap < 0 {other.ap = 0;}
other.killer_id = l_id;
other.kill_type = 5;
plr_send_effect(1, other.x, other.y);
with (other) {plr_send_stat(); plr_send_speed(); plr_send_pain();}
