arguments.argument:
if global.mp_knockback
{
    if other.x < x {other.hsp = -8;}
    if other.x > x {other.hsp = 8;}   
    if other.y > y {other.vsp = 8;} 
    if other.y < y {other.vsp = -8;} 
    with (other) {plr_send_speed();}
}
if other.pt > 0 {exit;}
if !variable_local_exists('l_id') {exit;}

var dmg, dist;
dist = round(point_distance(x, y, other.x, other.y)/1.25);
if !global.mp_godmode && !other.st_inv {dmg = 100 - dist;} else {dmg = 0;}
if dmg < 0 {dmg = 8;}
if !global.mp_gamemode  || global.mp_ffire || l_team != other.cl_team || (other.cl_id == l_id && global.mp_selfdamage) {with (other) {plr_hurt(dmg)};}
other.killer_id = l_id;
other.kill_type = 5;
plr_send_effect(1, other.x, other.y);
with (other) {plr_send_stat(); if !global.mp_godmode && !st_inv {plr_send_pain();};}
