arguments.argument:
if !variable_local_exists('l_id') || other.pt > 1 {exit;}
if collision_line(x, y, other.x, other.y, o_solid, 0, 0) || l_id == other.cl_id {exit;}
if !global.mp_godmode && !other.st_inv
{
if other.ap > 0
{
    other.hp -= 75;
    other.ap -= 150;
}
else
{
    other.hp -= 150;
}
if other.ap < 0 {other.ap = 0;}
other.kill_type = 7;
other.killer_id = l_id;
}
if other.x < x {other.hsp = -6;}
if other.x > x {other.hsp = 6;}   
if other.y > y {other.vsp = 6;} 
if other.y < y {other.vsp = -6;} 
with (other)
{
    plr_send_stat();
    plr_send_pain();
    plr_send_speed();
    plr_send_effect(1, x, y);
    plr_send_effect(7, x, y);
}
