arguments.argument:
if !variable_local_exists('l_id') {exit;}
if !variable_local_exists('p_type') {exit;}
if other.cl_id == l_id {exit;}
if p_type == 1 || p_type == 3 {instance_destroy(); exit;}
if !other.st_inv && !global.mp_godmode
{
    with (other) {plr_hurt(15)};
}
other.killer_id = l_id;
other.kill_type = 6;
if global.mp_knockback
{
    if other.x < x {other.hsp -= 1;}
    if other.x > x {other.hsp += 1;}   
    if other.y > y + 8 {other.vsp += 1;} 
    if other.y < y - 8 {other.vsp -= 1;} 
}
with (other) {plr_send_stat();  if !global.mp_godmode && !st_inv {plr_send_pain();}; plr_send_speed();}
plr_send_effect(1, other.x, other.y);
instance_destroy();
//instance_create(x, y, o_explosion);
//plr_send_explosion(cl_id, 1, x, y);
