arguments.argument:
if water {exit;}
switch p_type
{
    case 1:
    //rocket
        e = instance_create(x, y, o_explosion);
        e.l_id = l_id;
        e.l_team = l_team;
        plr_send_xplos(l_id, 1, x, y);
        plr_send_effect(4, x, y);
    break;
    case 2:
    //plasma
        plr_send_effect(5, x, y);
        plr_send_sound(25, x, y);
    break;
    case 3:
    //bfg
        bfg_hit();
        plr_send_xplos(l_id, 2, x, y);
    break;
}
