arguments.argument:
if !variable_local_exists('p_type') {exit;}
if place_meeting(x, y, o_water) || place_meeting(x, y, o_acid) {speed = 2.35; if p_type > 1 {water = 1; instance_destroy();}} else {speed = p_spd;}
if x < 0 || y < 0 || x > global.map_w || y > global.map_h {instance_destroy();}
if place_meeting(x, y, o_lift_up) {vspeed -= 0.5;}
if place_meeting(x, y, o_lift_down) {vspeed += 0.5;}
if place_meeting(x, y, o_lift_left) {hspeed -= 1; if direction == 0  {speed -= 1;}; if direction == 180  {speed += 1;}}
if place_meeting(x, y, o_lift_right) {hspeed -= 1; if direction == 0  {speed += 1;}; if direction == 180  {speed -= 1;}}
if p_type == 1 {image_angle = direction;}
