arguments.argument:
if !variable_local_exists('p_id') {exit;}
if other.cl_id == p_id || other.st_inv || global.mp_godmode || collision_line(x, y, other.x, other.y, o_solid, 0, 0) {exit;}
if !global.mp_gamemode  || global.mp_ffire || p_team != other.cl_team  {with (other) {plr_hurt(other.dmg)};}
other.killer_id = p_id;
other.kill_type = 13 + p_t;
plr_send_effect(1, other.x, other.y);
if kb && global.mp_knockback
{
    if other.x < x {other.hsp = -8; other.vsp = -6;}
    if other.x > x {other.hsp = 8; other.vsp = -6;}   
}
with (other) {plr_send_stat(); plr_send_speed();  if !global.mp_godmode && !st_inv {plr_send_pain();} other.pt = 10;}
