arguments.argument:
if !variable_local_exists('p_id') {exit;}
if other.pt > 0 || other.cl_id == p_id || other.st_inv || global.mp_godmode || collision_line(x, y, other.x, other.y, o_solid, 0, 0) {exit;}
if other.ap > 0
{
    other.hp -= round(dmg/2);
    other.ap -= dmg;
}
else
{
    other.hp -= dmg;
}
if other.ap < 0 {other.ap = 0;}
other.killer_id = p_id;
other.kill_type = 13 + p_t;
plr_send_effect(1, other.x, other.y);
if kb && global.mp_knockback
{
    if other.x < x {other.hsp = -8; other.vsp = -6;}
    if other.x > x {other.hsp = 8; other.vsp = -6;}   
}
with (other) {plr_send_stat(); plr_send_speed(); plr_send_pain(); other.pt = 10;}
